INSERT INTO `com_datos_ec` VALUES (2, 'Italia 2009', '<p class="titular">DATOS ECONÓMICOS</p>\r\n                <h2>Balance Económico Nacional de Italia<br />\r\n				<span style="font-size:9px">(variaciones % anuales valores concatenados- año 2000) </span>                </h2>\r\n                <div class="table4">\r\n                  <div class="filatitle12"><strong>Variables</strong></div>\r\n                  <div class="filatitle11"><strong>2004</strong></div>\r\n                  <div class="filatitle11"><strong>2005</strong></div>\r\n                  <div class="filatitle11"><strong>2006</strong></div>\r\n                  <div class="filatitle11"><strong>2007</strong></div>\r\n                  <div class="filatitle11"><strong>2008</strong> (*)</div>\r\n                  \r\n                  \r\n                  <div class="contentrad">\r\n                    <div class="filatitle12">PIB</div>\r\n                    <div class="filatitle11">1,5</div>\r\n                    <div class="filatitle11">0,6</div>\r\n                    <div class="filatitle11">1,8</div>\r\n                    <div class="filatitle11">1,5</div>\r\n                    <div class="filatitle11">-0,6a</div>\r\n                  </div>\r\n                  <div class="contentrad">\r\n                    <div class="filatitle12">Importaciones</div>\r\n                    <div class="filatitle11">4,2</div>\r\n                    <div class="filatitle11">2,2</div>\r\n                    <div class="filatitle11">5,9</div>\r\n                    <div class="filatitle11">4,4</div>\r\n                    <div class="filatitle11">8,1</div>\r\n                  </div>\r\n                  <div class="contentrad">\r\n                    <div class="filatitle12">Exportaciones</div>\r\n                    <div class="filatitle11">4,9</div>\r\n                    <div class="filatitle11">1,0</div>\r\n                    <div class="filatitle11">6,2</div>\r\n                    <div class="filat